import { Employee } from './employee';

export interface Workflow {
    id: string;
    type: string;
    details: {
        task: string;
        additionalInfo: string;
        requestInfo?: {
            id: string;
            type: string;
            status: string;
        };
    };
    status: WorkflowStatus;
    datetimecreated: string;
    employee?: {
        id: number;
        employeeId: string;
        fullName: string;
    };
    isUpdating?: boolean;
    isDeleting?: boolean;
}

export interface WorkflowsByEmployeeResponse {
    employee: {
        id: number;
        employeeId: string;
        fullName: string;
    };
    workflows: Workflow[];
}

export enum WorkflowStatus {
    ForReviewing = 'ForReviewing',
    Completed = 'Completed'
}

export enum WorkflowType {
    LeaveRequest = 'Leave Request',
    EquipmentRequest = 'Equipment Request',
    DepartmentChange = 'Department Change',
    Other = 'Other'
}

export interface WorkflowDetails {
    task: string;
    additionalInfo?: string;
}

export class WorkflowItem {
    id: string;
    name: string;
    quantity: number;
}
